    };
  },
  computed: {
    /* count() {
      console.log(this.$store.state.menuList);
      return this.$store.state.menuList;
    } */
  },
  methods: {
    jump() {
      this.$store.commit("updatePath", this.$route.path);
      this.$router.push("/transition");
    },
    Cancel() {
      MessageBox.confirm("确定执行此操作?").then(action => {
        if (action == "confirm") {
          //这里执行成功回调
          Indicator.open({
            text: "请稍等...",
            spinnerType: "fading-circle"
          });
          setTimeout(function() {
            Indicator.close();
            MessageBox("提示", "操作成功");
          }, 3000);
        } else {
          console.log(2);
        }
      });
    },
    init() {
      console.log(this.ShopCartList.length, 222);
      console.log(this.ShopCartList[0].name != null);
    },
    rollback() {
      this.$router.push("/order");
    },
    CputeSum() {
      var prices = 0;
      for (var item of this.ShopCartList) {
        prices += item.amount * item.num;
      }
      this.price = prices;
    }
  },
  created() {
    this.init();
    this.CputeSum();
  }
};
</script>
